<?phpclass Chiper{	function __construct()	{	}	public static function passwordEncode($password) {        return md5($password);	}	public static function generatePassword() {		$password = '';		$passLen = rand(6, 16);		for ($i = 0; $i < $passLen; $i++)		{			if (rand(0,35)<25)			{				$password = $password.chr(rand(97, 122));			}			else			{				$password = $password.chr(rand(48, 57));			}		}		return $password;	}	public static function generateSmallPassword() {		$password = '';		$passLen = rand(5, 8);		for ($i = 0; $i < $passLen; $i++)		{			if (rand(0,35)<25)			{				$password = $password.chr(rand(97, 122));			}			else			{				$password = $password.chr(rand(48, 57));			}		}		return $password;	}	public static function GUID() {		if (function_exists('com_create_guid') === true)		{			return trim(com_create_guid(), '{}');		}		return sprintf('%04X%04X-%04X-%04X-%04X-%04X%04X%04X',			mt_rand(0, 65535),			mt_rand(0, 65535),			mt_rand(0, 65535),			mt_rand(16384, 20479),			mt_rand(32768, 49151),			mt_rand(0, 65535),			mt_rand(0, 65535),			mt_rand(0, 65535)		);	}}