<?phpclass DB{	public static $DB;	private static $created;	function __construct() {        if (!self::$created) {            self::$DB = new mysqli('localhost', 'root', '', 'tropina');            self::$DB ->set_charset ( 'utf8' );            if (self::$DB->connect_errno) {                echo "Connect failed: %s\n", self::$DB->connect_error;                exit();            } else {                self::$created = true;            }        }	}}