<?php$Auth = new AdminAuth();$isAuth = $Auth->authCheck();if ($isAuth) {    $RESPONSE['gallery'] = array();    $Gallery = new Gallery();    $RESPONSE['gallery']['colors'] = $Gallery->getColors();    $RESPONSE['gallery']['categories'] = $Gallery->getCategories();    $RESPONSE['gallery']['megaCategories'] = $Gallery->getMegaCategories();} else {    $RESPONSE['error'] = 1;    $RESPONSE['errorMessage'] = 'not authorized';}