<?php$RESPONSE['gallery'] = array();$Gallery = new Gallery();$RESPONSE['gallery']['colors'] = $Gallery->getColors();$Akril = new Akril;$P = $Akril->getP();$Auth = new AdminAuth();$isAuth = $Auth->authCheck();if (!$isAuth) {    for ($i=0; $i<count($RESPONSE['gallery']['colors']); $i++) {        $RESPONSE['gallery']['colors'][$i]['CW'] = round(1000*$RESPONSE['gallery']['colors'][$i]['CW'])/1000;        $RESPONSE['gallery']['colors'][$i]['cost'] = $RESPONSE['gallery']['colors'][$i]['cost']*$RESPONSE['gallery']['colors'][$i]['CW']*$P;        unset($RESPONSE['gallery']['colors'][$i]['CW']);    }}$RESPONSE['gallery']['categories'] = $Gallery->getCategories();$RESPONSE['gallery']['megaCategories'] = $Gallery->getMegaCategories();