<?phpclass Artikles{	public static $DB;		function __construct()	{		new DB();		session_start();	}	public function getArtikles() {        $a = array();        $stmt = DB::$DB->prepare(            "SELECT `path`, `name`, `category_name`, `category_title` FROM artikles"        );        $stmt->execute();        $stmt->bind_result($a['path'], $a['name'], $a['categoryName'], $a['categoryTitle']);        $artikles = array();        $tr = $stmt->fetch();        $i = 0;        while($tr) {            $artikles[$i] = array();            $artikles[$i]['path'] = $a['path'];            $artikles[$i]['name'] = $a['name'];            $artikles[$i]['categoryName'] = $a['categoryName'];            $artikles[$i]['categoryTitle'] = $a['categoryTitle'];            $tr = $stmt->fetch();            $i++;        }        $stmt->close();        return $artikles;	}	public function addArtikle($name, $path, $categoryName, $categoryTitle)	{		$n = false;		$stmt = DB::$DB->prepare(			"SELECT `path` FROM artikles WHERE			`path` = ?;"		);		$stmt->bind_param('s', $path);		$stmt->execute();		$stmt->bind_result($n);		$stmt->fetch();		$stmt->close();		if (!$n) {			$stmt = DB::$DB->prepare("				INSERT INTO artikles					(`name`,					`path`,					`category_name`,					`category_title`)				VALUES					(?, ?, ?, ?) "			);			$stmt->bind_param('ssss',				$name,				$path,				$categoryName,				$categoryTitle			);			$stmt->execute();			$stmt->close();			return DB::$DB->insert_id;		} else {			$stmt = DB::$DB->prepare("				UPDATE artikles SET					`name` = ?,					`path` = ?,					`category_name` = ?,					`category_title` = ?				WHERE					`path` = ?"			);			$stmt->bind_param('sssss',				$name,				$path,				$categoryName,				$categoryTitle,				$path			);			$stmt->execute();			$stmt->close();			return DB::$DB->affected_rows;		}	}	public function deleteArtikle($path)	{		$stmt = DB::$DB->prepare("			DELETE FROM `artikles`			WHERE `path` = ? "		);		$stmt->bind_param('s',			$path		);		$stmt->execute();		$stmt->close();		return true;	}}